description:
p { 
 color: blue; 
}

#header { 
 background-color: grey; 
}

.highlight { 
 background-color: yellow; 
}

input[type='text'] { 
 border: 1px solid black; 
}

div p { 
 color: green; 
}

ul > li { 
 list-style-type: none; 
}

h1 + p { 
 font-size: 20px; 
}

h2 ~ p { 
 color: purple; 
}

a:hover { 
 color: red; 
}

p::first-line { 
 font-weight: bold; 
}

h1, h2, h3 { 
 font-family: Arial, sans-serif; 
}

* { 
 margin: 0; 
 padding: 0; 
}

ul.menu li.active a { 
 color: red; 
}
