description:
console.log(x); // Output: undefined (because declaration is hoisted) 
var x = 10; 
console.log(x); // Output: 10

greet(); // Output: Hello! 

function greet() { 
 console.log('Hello!'); 
}

console.log(sayHello); // Output: undefined 
var sayHello = function() { 
 console.log('Hello!'); 
}; 
sayHello(); // Output: Hello!

function testHoisting() { 
 console.log(a); // undefined (because of var hoisting) 
 console.log(b); // ReferenceError (because let is hoisted but in TDZ) 
 console.log(c); // ReferenceError (because const is hoisted but in TDZ) 

 var a = 1; 
 let b = 2; 
 const c = 3; 

 console.log(a); // 1 
 console.log(b); // 2 
 console.log(c); // 3 
} 
testHoisting();
